func:
if (msg.payload.message) {
    msg.payload = msg.payload.message.trim();
} else {
    msg.payload = "";
}
return msg;

// Split original AIS message into separate lines using <br>
var i;
msg.originalAisMessage_text = "";
for (i=0;i<msg.originalAisMessage.length;i++) {
    if (i>0) msg.originalAisMessage_text += "<br>";
    msg.originalAisMessage_text += escapeHtml(msg.originalAisMessage[i]);
}

// Now to get round the limitations of mustache...
// Ensure uninterpreted numbers which might be 0 are textified for display
if (!isNaN(msg.payload.turningRate)) msg.payload.turningRate = String(msg.payload.turningRate);
if (!isNaN(msg.payload.speedOverGround)) msg.payload.speedOverGround = String(msg.payload.speedOverGround);
if (!isNaN(msg.payload.courseOverGround)) msg.payload.courseOverGround = String(msg.payload.courseOverGround);
if (!isNaN(msg.payload.trueHeading)) msg.payload.trueHeading = String(msg.payload.trueHeading);
if (!isNaN(msg.payload.latitude)) msg.payload.latitude = String(msg.payload.latitude);
if (!isNaN(msg.payload.longitude)) msg.payload.longitude = String(msg.payload.longitude);
if (!isNaN(msg.payload.altitude)) msg.payload.altitude = String(msg.payload.altitude);
if (!isNaN(msg.payload.sequenceNumber)) msg.payload.sequenceNumber = String(msg.payload.sequenceNumber);
if (!isNaN(msg.payload.timeStampSeconds)) msg.payload.timeStampSeconds = String(msg.payload.timeStampSeconds);
if (!isNaN(msg.payload.version)) msg.payload.version = String(msg.payload.version);
if (!isNaN(msg.payload.shipId)) msg.payload.shipId = String(msg.payload.shipId);
if (!isNaN(msg.payload.dimensionToBow)) msg.payload.dimensionToBow = String(msg.payload.dimensionToBow);
if (!isNaN(msg.payload.dimensionToStern)) msg.payload.dimensionToStern = String(msg.payload.dimensionToStern);
if (!isNaN(msg.payload.dimensionToPort)) msg.payload.dimensionToPort = String(msg.payload.dimensionToPort);
if (!isNaN(msg.payload.dimensionToStarboard)) msg.payload.dimensionToStarboard = String(msg.payload.dimensionToStarboard);
if (!isNaN(msg.payload.draught)) msg.payload.draught = String(msg.payload.draught);
if (!isNaN(msg.payload.designatedAreaCode)) msg.payload.designatedAreaCode = String(msg.payload.designatedAreaCode);
if (!isNaN(msg.payload.functionalId)) msg.payload.functionalId = String(msg.payload.functionalId);
if (!isNaN(msg.payload.numberOfPersons)) msg.payload.numberOfPersons = String(msg.payload.numberOfPersons);

// Ensure boolean flags are textified for display
if (msg.payload.raim!==undefined) msg.payload.raim = String(msg.payload.raim);
if (msg.payload.retransmitted!==undefined) msg.payload.retransmitted = String(msg.payload.retransmitted);
if (msg.payload.offPosition!==undefined) msg.payload.offPosition = String(msg.payload.offPosition);
if (msg.payload.virtualAid!==undefined) msg.payload.virtualAid = String(msg.payload.virtualAid);
if (msg.payload.assignedMode!==undefined) msg.payload.assignedMode = String(msg.payload.assignedMode);

// Escape risky strings
if (msg.payload.name) msg.payload.name = escapeHtml(msg.payload.name);
if (msg.payload.destination) msg.payload.destination = escapeHtml(msg.payload.destination);

// Ready for the mustache template
return msg;

function escapeHtml(s) {
    var p = s.replace(/&/,"&amp;");
    var r = p.replace(/</g,"&lt;");
    return r.replace(/>/g,"&gt;");
}
